mapper.select:
select
  id, type, title, remote_addr, username, request_uri, http_method, class_method, params, session_id, response, use_time, browser, area, province, city, isp, exception, create_by, create_date, update_by, update_date, remarks, del_flag
from my_exam_test.sys_log


        and id = #{id}


        and type = #{type}


        and title = #{title}


        and remote_addr = #{remoteAddr}


        and username = #{username}


        and request_uri = #{requestUri}


        and http_method = #{httpMethod}


        and class_method = #{classMethod}


        and params = #{params}


        and session_id = #{sessionId}


        and response = #{response}


        and use_time = #{useTime}


        and browser = #{browser}


        and area = #{area}


        and province = #{province}


        and city = #{city}


        and isp = #{isp}


        and exception = #{exception}


        and create_by = #{createBy}


        and create_date = #{createDate}


        and update_by = #{updateBy}


        and update_date = #{updateDate}


        and remarks = #{remarks}


        and del_flag = #{delFlag}
